import React, { useState,useEffect} from 'react';
import { Link } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { selectUserData } from '../../redux/reducers/userReducer';
import axios from 'axios';
import { Cookies } from 'react-cookie';
import '../css/UserPosts.css';

const UserAnalyze = () => {
  const userData = useSelector(selectUserData);
  const [posts,setposts] = useState([]);
  const [postCount,setpostCount] = useState(0);

  const cookies = new Cookies();
  const jwtToken = cookies.get('Authorization');

  const fetchPosts = async () => {
    const bearerToken = jwtToken.startsWith('Bearer ') ? jwtToken : `Bearer ${jwtToken}`;

    try {
      const response = await axios.get(`http://localhost:8080/analyze/MyPage`,{
        headers: {
          'Content-Type': 'application/json',
          'Authorization': bearerToken,
        },
      });
      
      setposts(response.data)
      setpostCount(response.data.length);
    } catch (error) {
      console.error('Error fetching posts:', error);
    }
  };
  
  useEffect(() => {
    if(jwtToken){
      fetchPosts();
    }
  }, []);


  return (
    <div className='postList-container'>
      <div className='title'>
        <h2> 나의 분석</h2>
        <h2>{postCount}개</h2>
      </div>
      <div className='list'>
        <ul>
          {posts.map(post => (
            <li key={post.id}>
                {post.createdDate}
                {post.year}
                {post.month}
                {post.subject}
                {post.type}
                {post.analysisText}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default UserAnalyze;
